import { TypedEventTarget } from 'typescript-event-target';

declare const VOLUME_CHANGE = "volumeChange";
declare class VolumeChangeEvent extends Event {
    volume: number;
    constructor(volume: number);
}
declare class PCMPlayer extends TypedEventTarget<{
    [VOLUME_CHANGE]: VolumeChangeEvent;
}> {
    private audioContext;
    private playbackTime;
    private gainNode;
    constructor(audioContext: AudioContext);
    playAudio(data: Int16Array | Float32Array): void;
    get volumePercentage(): number;
    set volumePercentage(percentage: number);
}

export { PCMPlayer, VolumeChangeEvent };
